import React, { useState } from "react";
import './faq.css';

const faqData = [
  {
    question: "What is performance marketing, and how can Blueslag Technologies help my business?",
    answer: "Performance marketing focuses on measurable results such as clicks, conversions, and sales. Blueslag Technologies specializes in creating full-funnel marketing strategies that drive tangible outcomes for businesses, from lead generation to customer acquisition and retention."
  },
  {
    question: "When will this workshop be launched",
    answer: "You get instant access as soon as you enroll."
  },
  {
    question: "Are these the same recordings of live sessions I took?",
    answer: "Yes, they are recordings of previous live sessions."
  },
  {
    question: "Will I have live time access?",
    answer: "Yes, you will have lifetime access."
  },
  {
    question: "Is this beginner friendly?",
    answer: "Absolutely, it is designed for beginners."
  },
  {
    question: "Will you upsell anything in this course?",
    answer: "No, everything you need is included in this course."
  },
  {
    question: "Can I ask questions to Deep?",
    answer: "Yes, you can reach out for support and questions."
  }
];

export default function FaqData() {
  const [openIndex, setOpenIndex] = useState(null);

  const toggleFAQ = (index) => {
    setOpenIndex(openIndex === index ? null : index);
  };

  return (
    <div className="page-container">
      <div className="content">
        <h2>Still Not Convinced?</h2>
        <p>Go to my Instagram, and check the kind of content I make. Check out my highlights, and see the proofs uploaded there.</p>
        <p>I am very sure that this is the best investment you will ever do for learning ecommerce.</p>
        <p>After spending this money, I promise aapko kahi aur kuch sikhne nahi jaana padega. I will pour all my heart into making you a master at ecommerce.</p>
        <button className="join-btn">Join at â‚¹999/-</button>
        <p className="note">PS: This might be a rare occurrence. Not sure when will I ever teach again.</p>
      </div>

      <div className="faq-section">
        <h2>Frequently Asked Questions (FAQs)</h2>
        <p>
          Trying my best to answer most questions. In case you have more doubts, feel free to reach out at{" "}
          <a href="mailto:learn@9figures.club">learn@9figures.club</a>
        </p>
        <div className="faq-list">
          {faqData.map((faq, index) => (
            <div
              key={index}
              className={`faq-item ${openIndex === index ? "open" : ""}`}
              onClick={() => toggleFAQ(index)}
            >
              <div className="faq-question">{faq.question}</div>
              {openIndex === index && <div className="faq-answer">{faq.answer}</div>}
            </div>
          ))}
        </div>
      </div>
    </div>
  );
}
